  app_acc_vec[i] <- confusionMatrix(data=as.factor(Doc_boot_pred_tr), reference = df_boot_tr$visits)$overall[1]

  acc_vec[i] <- 0.368*app_acc_vec[i] + 0.632*oob_acc_vec[i]
